import React, { Component } from 'react';
import ParticlesBg from 'particles-bg'
import Clarifai from 'clarifai'
import Navigation from './components/Navigation/Naviagtion'
import Logo from './components/Logo/Logo'
import Rank from './components/Rank/Rank'
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm'
import './App.css';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';

// window.process = {
//   env: {
//       NODE_ENV: 'development'
//   }
// }

window.process = {}

const app = new Clarifai.App({
  apiKey: "06b4cfb08b79472d80f968ffc7f4ac7f"
});

class App extends Component {
  constructor() {
    super();
    this.state = {
      input: "",
      imgUrl: "",
      box:{}
    }
  }

  calculateFaceLoaction = (data)=> {
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box
    console.log(clarifaiFace)
    const image= document.getElementById('inputimage')
    const width = Number(image.width)
    const height = Number(image.height)
    console.log(width,height)
    const newBox = {
      leftCol: Number(clarifaiFace.left_col * width),
      topRow : Number(clarifaiFace.top_row * height),
      rightCol: Number(width - (clarifaiFace.right_col*width)),
      bottomRow : Number(height - (clarifaiFace.bottom_row*height))
    }
    console.log(newBox)
    return (newBox)
  }

  displayFaceBox = (box) =>{
    console.log(box)
    this.setState({box:newBox})
    console.log(box)
  }

  onInputChange = (event) => {
    this.setState({input:event.target.value})
    //console.log(event.target.value)
  }

  onButtonSubmit = () => {
    this.setState({imgUrl:this.state.input})
    console.log("click")
    app.models
    .predict('face-detection', this.state.input)
      .then(response => {
        this.displayFaceBox(this.calculateFaceLoaction(response))
        console.log(response.outputs[0].data.regions[0].region_info.bounding_box)
      })
      .catch(err=> console.log(err))

  }

  render() {
    const boxData = this.state.box
    if(!boxData && !boxData.leftCol && !boxData.topRow && !boxData.rightCol && !boxData.bottomRow){
      return (
        <div><p>Loading</p></div>
      )
    }

    return (
      <div className="App">
        <ParticlesBg type="cobweb" bg={true} />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm 
          onInputChange={this.onInputChange}
          onButtonSubmit={this.onButtonSubmit}
        /><br/>
        <FaceRecognition box={boxData} imgUrl={this.state.imgUrl}/>
      </div>
    );
  }
}

export default App;
